import React, { useState } from "react";
import "./ProductSlider.css";

const categories = ["غذای سگ", "غذای گربه", "غذای پرنده", "غذای ماهی"];

const dummySlides = [
  {
    id: 1,
    name: "غذای سگ نژاد بزرگ",
    price: "۶۰۰,۰۰۰ تومان",
    image: "/images/dogfood1.png",
  },
  {
    id: 2,
    name: "غذای گربه بالغ",
    price: "۴۵۰,۰۰۰ تومان",
    image: "/images/catfood1.png",
  },
  {
    id: 3,
    name: "غذای پرنده قناری",
    price: "۳۰۰,۰۰۰ تومان",
    image: "/images/birdfood1.png",
  },
  {
    id: 4,
    name: "غذای ماهی آکواریومی",
    price: "۲۰۰,۰۰۰ تومان",
    image: "/images/fishfood1.png",
  },
  {
    id: 5,
    name: "غذای سگ توله",
    price: "۵۵۰,۰۰۰ تومان",
    image: "/images/dogfood2.png",
  },
  {
    id: 6,
    name: "غذای گربه نژاد خاص",
    price: "۵۰۰,۰۰۰ تومان",
    image: "/images/catfood2.png",
  },
  {
    id: 7,
    name: "غذای پرنده طوطی",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/birdfood2.png",
  },
  {
    id: 8,
    name: "غذای ماهی آب شور",
    price: "۲۵۰,۰۰۰ تومان",
    image: "/images/fishfood2.png",
  },
  {
    id: 9,
    name: "غذای سگ لوکس",
    price: "۷۰۰,۰۰۰ تومان",
    image: "/images/dogfood3.png",
  },
];

const Product = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const itemsPerPage = 3;

  const handleNext = () => {
    if (currentIndex + itemsPerPage < dummySlides.length) {
      setCurrentIndex(currentIndex + itemsPerPage);
    }
  };

  const handlePrev = () => {
    if (currentIndex - itemsPerPage >= 0) {
      setCurrentIndex(currentIndex - itemsPerPage);
    }
  };

  const visibleSlides = dummySlides.slice(
    currentIndex,
    currentIndex + itemsPerPage
  );

  return (
    <div className="product-container">
      {/* نوار بالا */}
      <div className="top-bar">
        <div className="top-right">پرفروش‌ترین محصولات</div>

        <div className="top-center">
          {categories.map((cat, i) => (
            <span key={i} className="category">
              {cat}
            </span>
          ))}
        </div>

        <div className="top-left">
          <button onClick={handlePrev} className="scroll-btn">▶</button>
          <button onClick={handleNext} className="scroll-btn">◀</button>
        </div>
      </div>

      {/* اسلایدر ۳ تایی */}
      <div className="slider static-slider">
        {visibleSlides.map((item, idx) => (
          <div key={idx} className="slide-card">
            <img src={item.image} alt={item.name} className="product-img" />
            <div className="product-name">{item.name}</div>
            <div className="product-price">{item.price}</div>
            <button className="add-to-cart-btn">افزودن به سبد خرید</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Product;
