import React from "react";

const products = [
  {
    id: 1,
    name: "غذای خشک گربه نوع ۱",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/catfood1.png",
  },
  {
    id: 2,
    name: "غذای خشک گربه نوع ۲",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/catfood2.png",
  },
  {
    id: 3,
    name: "غذای خشک گربه نوع ۳",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/catfood3.png",
  },
  {
    id: 4,
    name: "غذای خشک گربه نوع ۴",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/catfood4.png",
  },
  {
    id: 5,
    name: "غذای خشک گربه نوع ۵",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/catfood5.png",
  },
];

const ProductSlider = () => {
  const scrollRef = React.useRef(null);

  const scroll = (direction) => {
    if (!scrollRef.current) return;
    const scrollAmount = 220;
    if (direction === "left") {
      scrollRef.current.scrollBy({ left: -scrollAmount, behavior: "smooth" });
    } else {
      scrollRef.current.scrollBy({ left: scrollAmount, behavior: "smooth" });
    }
  };

  return (
    <div className="relative w-full px-4 py-6 bg-[#fdfdfd]">
      {/* نوار عنوان بالا */}
      <div className="bg-orange-400 text-white px-4 py-2 rounded-md mb-4 w-fit">
        <h2 className="text-lg font-bold">پرفروش‌ترین محصولات</h2>
      </div>

      {/* اسلایدر کارت‌ها */}
      <div
        ref={scrollRef}
        className="flex overflow-x-auto space-x-4 rtl:space-x-reverse scrollbar-hide pb-2"
      >
        {products.map((product) => (
          <div
            key={product.id}
            className="min-w-[200px] w-[200px] bg-white border border-gray-200 rounded-xl shadow hover:shadow-lg transition-all duration-200 flex-shrink-0 p-4"
          >
            <img
              src={product.image}
              alt={product.name}
              className="w-24 h-24 mx-auto object-contain mb-2"
            />
            <p className="text-center text-sm font-semibold">{product.name}</p>
            <p className="text-center text-orange-600 font-bold mt-1">{product.price}</p>
            <button className="bg-orange-300 mt-2 w-full px-3 py-1 rounded-full text-white hover:bg-orange-400">
              انتخاب گزینه‌ها
            </button>
          </div>
        ))}
      </div>

      {/* دکمه‌های چپ و راست */}
      <div className="absolute top-1/2 right-0 transform -translate-y-1/2 z-10">
        <button
          onClick={() => scroll("right")}
          className="bg-white border rounded-full p-2 shadow-md"
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-5 w-5 text-gray-600"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
          </svg>
        </button>
      </div>
      <div className="absolute top-1/2 left-0 transform -translate-y-1/2 z-10">
        <button
          onClick={() => scroll("left")}
          className="bg-white border rounded-full p-2 shadow-md"
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-5 w-5 text-gray-600"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default ProductSlider;
