// src/components/ProductSlider.
export default function ProductSlider({ currentSlide, nextSlide, prevSlide }) {
    const itemsPerPage = 3;
  
    const products = [
      { id: 1, name: "غذای خشک گربه نوع یک", price: "400.000 تومان", images: "/images/cat1.jpg" },
      { id: 2, name: "غذای خشک گربه نوع دو", price: "400.000 تومان", images: "/images/cat2.jpg" },
      { id: 3, name: "غذای خشک گربه نوع سه", price: "400.000 تومان", images: "/images/cat3.jpg" },
      { id: 4, name: "غذای خشک گربه نوع چهار", price: "400.000 تومان", images: "/images/cat4.jpg" },
      { id: 5, name: "غذای خشک گربه نوع پنج", price: "400.000 تومان", images: "/images/cat5.jpg" },
      { id: 6, name: "غذای خشک گربه نوع شش", price: "400.000 تومان", images: "/images/cat6.jpg" },
    ];
  
    return (
      <div className="container my-5">
        <h3 className="fw-bold mb-4">پرفروش‌ترین محصولات</h3>
        <div className="row">
          {products.slice(currentSlide, currentSlide + itemsPerPage).map(product => (
            <div key={product.id} className="col-md-4 mb-4">
              <div className="card h-100 shadow rounded-4 border-0">
                <img src={product.images} alt={product.name} className="card-img-top rounded-top-4" style={{ height: "250px", objectFit: "cover" }} />
                <div className="card-body text-center">
                  <h5 className="card-title fw-bold">{product.price}</h5>
                  <button className="btn btn-warning rounded-pill px-4 py-2 text-white fw-semibold">
                    افزودن به سبد خرید
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
