import React from "react";
import "./WatermelonBanner.css"; // اگه از CSS جدا استفاده می‌کنی

const WatermelonBanner = () => {
  return (
    <div className="watermelon-banner">
      <img src="/images/top.png" alt="هندونه" className="top-image" />
      <img src="/images/phone.png" alt="موبایل" className="phone-image" />

      <div className="content">
        <h2 className="title">یک شعبه از وودمارت در تلفن شماست!</h2>
        <p className="desc">
          لورم ایپسوم متن ساختگی با تولید سادگی نامفهوم از صنعت چاپ و با استفاده از
          طراحان گرافیک است. چاپگرها و متون بلکه روزنامه و مجله در ستون و سطرآنچنان که لازم است.
        </p>
        <div className="buttons">
          <button className="btn-primary">دریافت از مایکت</button>
          <button className="btn-secondary">دریافت از بازار</button>
        </div>
      </div>
    </div>
  );
};

export default WatermelonBanner;
