// ProductSlider.js
import { useRef } from "react";
import "./ProductSlider.css"; // استایل‌هاتو هم جدا کن

export default function ProductSlider() {
  const sliderRef = useRef(null);

  const scrollSlider = (direction) => {
    const scrollAmount = 250;
    if (direction === "next") {
      sliderRef.current.scrollLeft += scrollAmount;
    } else {
      sliderRef.current.scrollLeft -= scrollAmount;
    }
  };

  return (
    <>
      {/* تب‌ها */}
      <div className="product-tabs-wrapper my-4">
        <div className="product-tabs scroll-menu d-flex align-items-center justify-content-between gap-3 px-3 py-3 rounded shadow-sm">
          <div className="tab-item first-tab flex-shrink-0">پرفروش‌ترین محصولات</div>

          <div className="middle-tabs d-flex gap-2 flex-nowrap overflow-hidden">
            <div className="tab-item flex-shrink-0">غذای گربه</div>
            <div className="tab-item flex-shrink-0">غذای پرندگان</div>
            <div className="tab-item flex-shrink-0">غذای ماهی</div>
            <div className="tab-item flex-shrink-0">غذای سگ</div>
          </div>

          <div className="slider-buttons d-none d-md-flex gap-2">
            <button className="btn short-cute-btn small-btn" onClick={() => scrollSlider("prev")}>قبلی</button>
            <button className="btn short-cute-btn small-btn" onClick={() => scrollSlider("next")}>بعدی</button>
          </div>
        </div>
      </div>

      {/* اسلایدر کارت‌ها */}
      <div className="product-slider-wrapper mt-4">
        <div className="product-slider d-flex gap-3 overflow-hidden" ref={sliderRef}>
          <div className="product-card flex-shrink-0">کارت ۱</div>
          <div className="product-card flex-shrink-0">کارت ۲</div>
          <div className="product-card flex-shrink-0">کارت ۳</div>
          <div className="product-card flex-shrink-0">کارت ۴</div>
          <div className="product-card flex-shrink-0">کارت ۵</div>
          <div className="product-card flex-shrink-0">کارت ۶</div>
        </div>
      </div>
    </>
  );
}
