import React, { useState } from "react";
import "./Product.";

// محصولات آزمایشی (میتونی راحت تغییرشون بدی)
const products = [
  {
    id: 1,
    title: "غذای سگ با طعم مرغ",
    price: "۴۵۰,۰۰۰ تومان",
    image: "/images/dogfood1.png",
  },
  {
    id: 2,
    title: "غذای گربه با ماهی",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/catfood1.png",
  },
  {
    id: 3,
    title: "غذای پرنده قناری",
    price: "۱۵۰,۰۰۰ تومان",
    image: "/images/birdfood1.png",
  },
  {
    id: 4,
    title: "غذای ماهی آکواریومی",
    price: "۲۲۰,۰۰۰ تومان",
    image: "/images/fishfood1.png",
  },
  {
    id: 5,
    title: "غذای سگ با گوشت",
    price: "۵۰۰,۰۰۰ تومان",
    image: "/images/dogfood2.png",
  },
  {
    id: 6,
    title: "غذای گربه ارگانیک",
    price: "۴۲۰,۰۰۰ تومان",
    image: "/images/catfood2.png",
  },
];

const categories = ["غذای سگ", "غذای گربه", "غذای پرنده", "غذای ماهی"];

const Product = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const itemsPerPage = 3;

  const handleNext = () => {
    if (currentIndex + itemsPerPage < products.length) {
      setCurrentIndex(currentIndex + itemsPerPage);
    }
  };

  const handlePrev = () => {
    if (currentIndex - itemsPerPage >= 0) {
      setCurrentIndex(currentIndex - itemsPerPage);
    }
  };

  const visibleProducts = products.slice(currentIndex, currentIndex + itemsPerPage);

  return (
    <div className="product-container">
      {/* نوار بالا */}
      <div className="top-bar">
        <div className="top-right">پرفروش‌ترین محصولات</div>

        <div className="top-center">
          {categories.map((cat, i) => (
            <span key={i} className="category">
              {cat}
            </span>
          ))}
        </div>

        <div className="top-left">
          <button onClick={handlePrev} className="scroll-btn">◀</button>
          <button onClick={handleNext} className="scroll-btn">▶</button>
        </div>
      </div>

      {/* کارت‌ها */}
      <div className="slider static-slider">
        {visibleProducts.map((product) => (
          <div key={product.id} className="slide-card">
            <img src={product.image} alt={product.title} className="product-image" />
            <h3 className="product-title">{product.title}</h3>
            <p className="product-price">{product.price}</p>
            <button className="add-to-cart-btn">افزودن به سبد خرید</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Product;
