import React from "react";
import { Card, CardContent } from "@/components/ui/card";
import { ChevronLeft, ChevronRight } from "lucide-react";

const products = [
  {
    id: 1,
    name: "غذای خشک گربه نوع ۱",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/catfood1.png",
  },
  {
    id: 2,
    name: "غذای خشک گربه نوع ۲",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/catfood2.png",
  },
  {
    id: 3,
    name: "غذای خشک گربه نوع ۳",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/catfood3.png",
  },
  {
    id: 4,
    name: "غذای خشک گربه نوع ۴",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/catfood4.png",
  },
  {
    id: 5,
    name: "غذای خشک گربه نوع ۵",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/catfood5.png",
  },
];

const ProductSlider = () => {
  const scrollRef = React.useRef(null);

  const scroll = (direction) => {
    if (!scrollRef.current) return;
    const scrollAmount = 220;
    if (direction === "left") {
      scrollRef.current.scrollBy({ left: -scrollAmount, behavior: "smooth" });
    } else {
      scrollRef.current.scrollBy({ left: scrollAmount, behavior: "smooth" });
    }
  };

  return (
    <div className="relative w-full px-6 py-4">
      <h2 className="text-xl font-bold text-right mb-4">پرفروش‌ترین محصولات</h2>

      <div
        ref={scrollRef}
        className="flex overflow-x-auto space-x-4 rtl:space-x-reverse scrollbar-hide"
      >
        {products.map((product) => (
          <Card
            key={product.id}
            className="min-w-[200px] w-[200px] flex-shrink-0"
          >
            <CardContent className="flex flex-col items-center py-4">
              <img
                src={product.image}
                alt={product.name}
                className="w-24 h-24 object-contain mb-2"
              />
              <p className="text-center text-sm font-semibold">
                {product.name}
              </p>
              <p className="text-orange-600 font-bold mt-1">{product.price}</p>
              <button className="bg-orange-300 mt-2 px-3 py-1 rounded-full text-white hover:bg-orange-400">
                انتخاب گزینه‌ها
              </button>
            </CardContent>
          </Card>
        ))}
      </div>

      <div className="absolute top-1/2 right-0 transform -translate-y-1/2">
        <button
          onClick={() => scroll("right")}
          className="bg-white border rounded-full p-1 shadow-md"
        >
          <ChevronRight />
        </button>
      </div>
      <div className="absolute top-1/2 left-0 transform -translate-y-1/2">
        <button
          onClick={() => scroll("left")}
          className="bg-white border rounded-full p-1 shadow-md"
        >
          <ChevronLeft />
        </button>
      </div>
    </div>
  );
};

export default ProductSlider;
