import { useRef } from "react";
import "./ProductSlider.css"; // استایل جدا باشه

export default function ProductSlider() {
  const sliderRef = useRef(null);

  const scrollSlider = (direction) => {
    const scrollAmount = 300;
    if (direction === "next") {
      sliderRef.current.scrollLeft += scrollAmount;
    } else {
      sliderRef.current.scrollLeft -= scrollAmount;
    }
  };

  return (
    <div className="my-4">
      {/* دکمه‌ها */}
      <div className="d-flex justify-content-between mb-2">
        <button className="btn btn-outline-secondary" onClick={() => scrollSlider("prev")}>
          قبلی
        </button>
        <button className="btn btn-outline-secondary" onClick={() => scrollSlider("next")}>
          بعدی
        </button>
      </div>

      {/* اسلایدر کارت‌ها */}
      <div
        className="d-flex overflow-auto gap-3"
        ref={sliderRef}
        style={{ scrollBehavior: "smooth" }}
      >
        {Array.from({ length: 10 }).map((_, index) => (
          <div
            key={index}
            className="product-card flex-shrink-0 p-3 border rounded"
            style={{ minWidth: "200px", background: "#f8f9fa" }}
          >
            کارت {index + 1}
          </div>
        ))}
      </div>
    </div>
  );
}
