import React, { useState, useEffect } from "react";

const products = [
  { id: 1, name: "Dog Food", price: "$20" },
  { id: 2, name: "Cat Toy", price: "$10" },
  { id: 3, name: "Bird Cage", price: "$35" },
  { id: 4, name: "Fish Tank", price: "$50" },
  { id: 5, name: "Hamster Wheel", price: "$15" },
  { id: 6, name: "Rabbit Cage", price: "$45" },
];

function ProductSliderWithStaticCard() {
  const [startIndex, setStartIndex] = useState(0);

  const maxStartIndex = products.length - 3;

  useEffect(() => {
    const interval = setInterval(() => {
      setStartIndex((prevIndex) =>
        prevIndex < maxStartIndex ? prevIndex + 1 : 0
      );
    }, 3000); // هر ۳ ثانیه یک بار حرکت کن

    return () => clearInterval(interval); // پاک‌سازی تایمر
  }, []);

  const visibleProducts = products.slice(startIndex, startIndex + 3);

  return (
    <div className="container my-4">
      <div className="row g-3">
        {/* کارت ثابت */}
        <div className="col-md-3">
          <div className="card text-center h-100 shadow">
            <div className="card-body d-flex flex-column justify-content-between">
              <div>
                <h5 className="card-title text-danger fw-bold">آف پت وودمارت</h5>
              </div>
              <div>
                <button className="btn btn-warning my-3 fw-bold">اینجا کلیک کنید</button>
              </div>
              <div>
                <img
                  src="https://via.placeholder.com/100x100"
                  alt="Pet Sale"
                  className="img-fluid"
                />
              </div>
            </div>
          </div>
        </div>

        {/* اسلایدر کارت‌ها */}
        <div className="col-md-9">
          <div className="row slider-row transition">
            {visibleProducts.map((product) => (
              <div className="col-md-4" key={product.id}>
                <div className="card h-100 shadow-sm">
                  <div className="card-body text-center">
                    <h5 className="card-title">{product.name}</h5>
                    <p className="card-text text-success fw-bold">{product.price}</p>
                    <button className="btn btn-success btn-sm">Add to Cart</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductSliderWithStaticCard;
