import React, { useRef } from "react";
import "./Productس.css";

const categories = ["غذای سگ", "غذای گربه", "غذای پرنده", "غذای ماهی"];
const dummySlides = [1, 2, 3, 4, 5, 6];

const Product = () => {
  const scrollRef = useRef(null);

  const scroll = (direction) => {
    const amount = 250;
    if (!scrollRef.current) return;
    scrollRef.current.scrollBy({
      left: direction === "right" ? amount : -amount,
      behavior: "smooth",
    });
  };

  return (
    <div className="product-container">
      {/* نوار بالا */}
      <div className="top-bar">
        <div className="top-right">پرفروش‌ترین محصولات</div>

        <div className="top-center">
          {categories.map((cat, i) => (
            <span key={i} className="category">
              {cat}
            </span>
          ))}
        </div>

        <div className="top-left">
          <button onClick={() => scroll("left")} className="scroll-btn">◀</button>
          <button onClick={() => scroll("right")} className="scroll-btn">▶</button>
        </div>
      </div>

      {/* اسلایدر کارت‌ها */}
      <div className="slider" ref={scrollRef}>
        {dummySlides.map((num) => (
          <div key={num} className="slide-card">
            کارت {num}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Product;
