import React, { useRef, useState } from "react";

const categories = [
  "پرفروش‌ترین محصولات",
  "غذای گربه",
  "غذای سگ",
  "غذای پرندگان",
  "غذای ماهی",
];

const products = [
  {
    id: 1,
    name: "محصول ۱",
    category: "پرفروش‌ترین محصولات",
    price: "۴۰۰,۰۰۰ تومان",
    image: "/images/catfood1.png",
  },
  {
    id: 2,
    name: "محصول ۲",
    category: "غذای گربه",
    price: "۳۲۰,۰۰۰ تومان",
    image: "/images/catfood2.png",
  },
  {
    id: 3,
    name: "محصول ۳",
    category: "غذای سگ",
    price: "۲۵۰,۰۰۰ تومان",
    image: "/images/catfood3.png",
  },
  {
    id: 4,
    name: "محصول ۴",
    category: "غذای پرندگان",
    price: "۲۲۰,۰۰۰ تومان",
    image: "/images/catfood4.png",
  },
  {
    id: 5,
    name: "محصول ۵",
    category: "پرفروش‌ترین محصولات",
    price: "۵۰۰,۰۰۰ تومان",
    image: "/images/catfood5.png",
  },
];

const ProductSliderWithTabs = () => {
  const [selectedCategory, setSelectedCategory] = useState("پرفروش‌ترین محصولات");
  const scrollRef = useRef(null);

  const scroll = (direction) => {
    if (!scrollRef.current) return;
    const scrollAmount = 240;
    direction === "left"
      ? scrollRef.current.scrollBy({ left: -scrollAmount, behavior: "smooth" })
      : scrollRef.current.scrollBy({ left: scrollAmount, behavior: "smooth" });
  };

  const filteredProducts = products.filter(
    (p) => p.category === selectedCategory
  );

  return (
    <div className="bg-[#fffaf4] rounded-2xl p-4 shadow-md w-full">
      {/* نوار بالای دسته‌بندی */}
      <div className="flex flex-wrap justify-between items-center mb-4">
        <div className="flex gap-2 flex-wrap">
          {categories.map((cat) => (
            <button
              key={cat}
              onClick={() => setSelectedCategory(cat)}
              className={`px-4 py-1.5 rounded-full text-sm font-medium transition-all ${
                selectedCategory === cat
                  ? "bg-orange-400 text-white shadow"
                  : "bg-white text-gray-600 border"
              }`}
            >
              {cat}
            </button>
          ))}
        </div>

        {/* دکمه‌های بعدی و قبلی */}
        <div className="flex gap-2">
          <button
            onClick={() => scroll("right")}
            className="bg-orange-400 text-white px-4 py-1.5 rounded-full shadow hover:bg-orange-500"
          >
            بعدی
          </button>
          <button
            onClick={() => scroll("left")}
            className="bg-orange-400 text-white px-4 py-1.5 rounded-full shadow hover:bg-orange-500"
          >
            قبلی
          </button>
        </div>
      </div>

      {/* اسلایدر کارت‌ها */}
      <div
        ref={scrollRef}
        className="flex overflow-x-auto space-x-4 rtl:space-x-reverse pb-2 scrollbar-hide"
      >
        {filteredProducts.map((product) => (
          <div
            key={product.id}
            className="min-w-[200px] w-[200px] bg-white border border-gray-200 rounded-xl shadow-sm hover:shadow-lg p-4 flex-shrink-0 transition"
          >
            <img
              src={product.image}
              alt={product.name}
              className="w-24 h-24 mx-auto mb-2 object-contain"
            />
            <p className="text-center text-sm font-semibold">{product.name}</p>
            <p className="text-center text-orange-500 font-bold mt-1">
              {product.price}
            </p>
            <button className="bg-orange-300 mt-2 w-full px-3 py-1 rounded-full text-white hover:bg-orange-400">
              انتخاب گزینه‌ها
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductSliderWithTabs;
